import { citys } from '../mock-items.js/sitys'
import { GET_FIRST_DATA, ADD_CITY, CHANGE_FLAG } from './actions';

const initialState = []


export const citysReducer = (state = initialState, action) => {
    switch (action.type) {
        // case GET_FIRST_DATA: {
        //     return {
        //         data: action.payload.data,
        //     }
        // }
        case ADD_CITY: {
            const item = action.payload
            return [...state, item]
        }
        case CHANGE_FLAG: {
            const id = action.payload
            return [...state.map(el => {
                if (el.id === id) {
                    el.flag === !el.flag
                }
            })]
        }
        default:
            return state;
    }
}
