import { useEffect } from "react";
import { useSelector, useDispatch } from 'react-redux';
import { getData, setFilter } from "../redux/actions";


const FilterTable = () => {
    const dispatch = useDispatch();
    const filter = useSelector(state => state.table.filter);

    useEffect(() => {
        dispatch(getData())
    }, [dispatch])

    function uniqueItem(arr) {
        let result = [];
        for (let i = 0; i < arr.length; i++) {
            if (!result.includes(arr[i])) {
                result.push(arr[i]);
            }
        }
        return result;
    }

    const myClick = value => {
        console.log(value);
        dispatch(setFilter(value));
    }


    return (
        <form type="checkbox" id="myCheck" >
            <select id="select_" onChange={(item) => myClick(item)} >
                <option value="css" id='ff'  >select state</option>
                {uniqueItem(filter).map(item => (
                    <option onClick={() => myClick(item)} value="css" >{item}</option>
                ))}
            </select>
        </form >
    );
}

export default FilterTable;
