import { useEffect, useState } from "react";
import DataTable, { defaultThemes } from 'react-data-table-component';
import { getData } from "../redux/actions";
import { useSelector, useDispatch } from 'react-redux';


const columns = [
    {
        name: 'Id',
        selector: row => row.id,
        sortable: true,
    },
    {
        name: 'First Name',
        selector: row => row.firstName,
        sortable: true,
    },
    {
        name: 'Last Name',
        selector: row => row.lastName,
        sortable: true,
    },
    {
        name: 'Email',
        selector: row => row.email,
        sortable: true,
    },
    {
        name: 'Phone',
        selector: row => row.phone,
        sortable: true,
    },
    {
        name: 'State',
        selector: row => row.adress.state,
        sortable: true,
    },
];

const customStyles = {
    header: {
        style: {
            minHeight: '56px',
        },
    },
    headRow: {
        style: {
            borderTopStyle: 'solid',
            borderTopWidth: '1px',
            borderTopColor: defaultThemes.default.divider.default,
        },
    },
    headCells: {
        style: {
            '&:not(:last-of-type)': {
                borderRightStyle: 'solid',
                borderRightWidth: '1px',
                borderRightColor: defaultThemes.default.divider.default,
            },
        },
    },
    cells: {
        style: {
            '&:not(:last-of-type)': {
                borderRightStyle: 'solid',
                borderRightWidth: '1px',
                borderRightColor: defaultThemes.default.divider.default,
            },
        },
    },
};

const MainView = () => {
    const dispatch = useDispatch();
    const data = useSelector(state => state.table.data);
    const currentFilter = useSelector(state => state.table.currentFilter);

    const [tableData, setTableData] = useState(data);

    useEffect(() => {
        dispatch(getData())
    }, [dispatch])

    useEffect(() => {
        const value = data.filter(data => data.adress.state === currentFilter);
        setTableData(value)
    }, [currentFilter])

    useEffect(() => {
        setTableData(data);
    }, [data])

    const click = () => dispatch(getData());

    // let b = document.getElementsByClassName('sc-jrAFXE fjxduL rdt_TableRow').addEventListener('click', search);
    // a.addEventListener('click', search);

    // function search(event) {
    //     console.log(event.target);
    // }
    function handleClick(data) {
        const action = data.target.data
        console.log(action);
    }

    let keys = document.getElementsByClassName('sc-jrAFXE fjxduL rdt_TableRow');
    [...keys].forEach(value => value.addEventListener('click', handleClick));


    return (
        <div>
            <button onClick={click} >reset</button>
            <form >
                <DataTable
                    columns={columns}
                    data={tableData}
                    customStyles={customStyles}
                    pagination
                />
                <output name='result'></output>
            </form>
        </div>
    );
};
export default MainView;
