const initialState = {
    data: [],
    filter: [],
    currentFilter: '',
};

export default function tableReducer(state = initialState, action) {
    switch (action.type) {
        case 'GET_DATA_SUCCESSFULL':
            return {
                data: action.payload.data,
                filter: action.payload.filter,
            }

        case 'SET_FILTER':
            return {
                ...state,
                currentFilter: action.payload
            }
        default: return state
    }

}
