import { getData } from "../redux/actions";
import { useSelector, useDispatch } from 'react-redux';
import { useEffect } from "react";


const FilterTable = () => {
    const dispatch = useDispatch();
    const filter = useSelector(state => state.table.filter);

    useEffect(() => {
        dispatch(getData())
    }, [dispatch])

    return (
        <form className="FilterTable" action="action_form" target="_top">
            <select>
                <option disabled>Filter by state</option>
                <option value={filter}>WI</option>
                {/* <option value="css">TN</option>
                <option value="php">FL</option>
                <option value="js">NE</option> */}
            </select>
        </form>
    );
}

export default FilterTable;
