import { GET_FIRST_DATA, ADD_CITY, DELETE_CITY } from './actions';

const initialState = [{
    name: 'Kiyv',
    temp: 17 + '& degC',
    tempMax: 26 + '&degC',
    tempMin: 12 + '&degC',
    description: 'description',
    windSpeed: 127,
    windGust: 54,
    humidity: 84,
    pressure: 12,
    id: 1,
    flag: false
}]


export const citysReducer = (state = initialState, action) => {
    switch (action.type) {
        // case GET_FIRST_DATA: {
        //     return {
        //         data: action.payload.data,
        //     }
        // }
        case ADD_CITY: {
            const item = action.payload
            return [...state, item]
        }
        case DELETE_CITY: {
            const id = action.payload
            return [...state.filter(el => el.id !== id.id)]
        }
        default:
            return state;
    }
}
