import React, { useState } from "react";
import CityList from "./components/CitysList";
import './App.css';
import { citys } from './mock-items.js/sitys';
import { v4 as uuid } from 'uuid';
import { useSelector, useDispatch } from "react-redux";


function App() {

  let items = useSelector((state) => state.sitys)
  const dispatch = useDispatch()

  const [input, setInput] = useState('')

  const idNum = uuid();

  function addCity() {
    citys.push({
      city: input,
      temp: 17 + '& degC',
      tempMax: 26 + '&degC',
      tempMin: 12 + '&degC',
      description: 'description',
      windSpeed: 127,
      windGust: 54,
      humidity: 84,
      pressure: 12,
      id: idNum,
      flag: false
    });
    setInput('')
  }

  function addCity() {
    let t = document.getElementsByClassName('input').value
    console.log(t);
  }


  function handlyChange(event) {
    setInput(event.target.value);
  }



  return (
    <div className='main'>
      <div className='input'>
        <input type="text" placeholder='add city name' onChange={handlyChange} value={input} />
        <button onClick={() => addCity()}>add city</button>
      </div>
      <div><CityList /></div>
    </div>
  )
}

export default App;
