import { citys } from '../mock-items.js/sitys'
import { GET_FIRST_DATA, ADD_CIITY } from './actions';

const initialState = []


export const citysReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_FIRST_DATA: {
            return {
                data: action.payload.data,
            }
        }
        case ADD_CIITY: {
            const item = action.payload
            return [...state, item]
        }
        default:
            return state;
    }
}
