import { GET_FIRST_DATA, ADD_CITY, DELETE_CITY, UPDATE_CITY } from './actions';

// const initialState = []
let initialState;

fetch('https://api.openweathermap.org/data/2.5/weather?q=Kharkiv&units=metric&APPID=bcf45506de72468c2a0cb7f9a26542bc')
    .then(response => response.json())
    .then(data => initialState = [...data])




export const citysReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_CITY: {
            const item = action.payload
            return [...state, item]
        }
        case DELETE_CITY: {
            const id = action.payload
            return [...state.filter(el => el.data.id !== id)]
        }
        case GET_FIRST_DATA:
            return {
                data: action.payload.data,
            }
        case UPDATE_CITY: {
            const id = action.payload.id
            const data = action.payload.data
            return [...state.filter(el => el.id !== id), data]
        }
        default:
            return state;
    }
}
