import { useDispatch } from "react-redux";

const dispatch = useDispatch()

export const getFirstData = () => dispatch => {
    fetch('https://api.openweathermap.org/data/2.5/weather?q=Kharkiv&units=metric&APPID=bcf45506de72468c2a0cb7f9a26542bc')
        .then(response => response.json())
        .then(data => {
            dispatch({ type: 'GET_FIRST_DATA', payload: { data } })
        }
        );
};


export const ADD_CITY = 'ADD_CITY'
export const DELETE_CITY = 'DELETE_CITY'
export const LOAD_CITY = 'LOAD_CITY'
export const GET_FIRST_DATA = 'GET_FIRST_DATA'
