import React, { useState } from "react";
import CityList from './CitysList'
import { citys } from '../mock-items.js/sitys'
import AutorenewIcon from '@mui/icons-material/Autorenew';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import CloseIcon from '@mui/icons-material/Close';
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";


function CityWeather() {

    let items = useSelector((state) => state.sitys)
    const dispatch = useDispatch()
    console.log(items);

    const [show, setShow] = useState(true)

    function showCitys() {
        showCity[0].flag = !items.flag;
        setShow(!show)
    }

    // function deleteCity() {
    //     for (let i of citys) {
    //         if (i.id === items.id) {
    //             citys.splice(citys.indexOf(i), 1);
    //         }
    //     }
    //     showCitys();
    // }

    function deleteCity() {
        dispatch({
            type: 'DELETE_CITY',
            payload: {
                id: items.id,
            }
        })
    }

    let showCard = <div><CityList /></div>

    if (show) {
        showCard =
            <div className='showcity'>
                <div className='close'><CloseIcon onClick={() => showCitys()} /></div>
                <div className='showcity-name'>
                    < AutorenewIcon sx={{ fontSize: 30 }} onClick={() => console.log('update')} cursor='pointer' />
                    {items.city}
                    <DeleteForeverIcon color="warning" sx={{ fontSize: 40 }} onClick={() => deleteCity()} cursor='pointer' />
                </div>
                <div className='showcity-description'>
                    <ul className='ul'>
                        <li> temp - {items.temp} </li>
                        <li>tempMax - {items.tempMax}</li>
                        <li> tempMin - {items.tempMin}</li>
                        <li> description - {items.description}</li>
                        <li>windSpeed - {items.windSpeed}</li>
                        <li>windGust - {items.windGust}</li>
                        <li>humidity - {items.humidity}</li>
                        <li> pressure - {items.pressure}</li>
                    </ul>
                </div>
            </div>
    }

    return (
        <div >{showCard}</div>
    )
}


export default CityWeather;
