import { useEffect } from "react";
import DataTable from 'react-data-table-component';
import { getData } from "../redux/actions";
import { useSelector, useDispatch } from 'react-redux';
//import DataTable, { defaultThemes } from '../../src/index';


const columns = [
    {
        name: 'Id',
        selector: row => row.id,
        sortable: true,
    },
    {
        name: 'First Name',
        selector: row => row.firstName,
        sortable: true,
    },
    {
        name: 'Last Name',
        selector: row => row.lastName,
        sortable: true,
    },
    {
        name: 'Email',
        selector: row => row.email,
        sortable: true,
    },
    {
        name: 'Phone',
        selector: row => row.phone,
        sortable: true,
    },
    {
        name: 'State',
        selector: row => row.state,
        sortable: true,
    },
];

const customStyles = {
    body: {
        style: {
            higth: '100px',
            width: '300px',
        },
    },

    header: {
        style: {
            borderrStyle: 'solid',
            border: '1px',
            minHeight: '56px',
        },
    },
    headRow: {
        style: {
            borderTopStyle: 'solid',
            borderTopWidth: '1px',
            borderColor: 'black',
        },
    },
    headCells: {
        style: {
            borderRightStyle: 'solid',
            borderRightWidth: '1px',
            borderColor: 'black',
        },
    },
    cells: {
        style: {
            borderRightStyle: 'solid',
            borderRightWidth: '1px',
            borderColor: 'black',
        },
    },
    rows: {
        style: {
            borderRightStyle: 'solid',
            borderRightWidth: '1px',
            borderColor: 'black',
        },
    },
};

const MainView = () => {
    const dispatch = useDispatch();
    const data = useSelector(state => state.table.data);

    useEffect(() => {
        dispatch(getData())
    }, [dispatch])


    return (
        <DataTable
            columns={columns}
            data={data}
            customStyles={customStyles}
            pagination
        />
    );
};
export default MainView;
