import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { fetchCount } from './counterAPI';

const initialState = {
    data: []
};

export default function tableReducer(state = initialState, action) {
    switch (action.type) {
        case VERIFIED_PROFILE_LOADING:
            return update(state, {
                verifiedProfileStatus: { $set: loadingStatus(action.payload) },
                profileLoader: { $set: loadingStatus(action.payload) },
            })
        default:
            return state
    }
}
