import React, { useState } from "react";
import CityList from './CitysList'
import { citys } from '../mock-items.js/sitys'
import AutorenewIcon from '@mui/icons-material/Autorenew';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import CloseIcon from '@mui/icons-material/Close';
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";


function CityWeather() {

    let items = useSelector((state) => state.sitys)
    const dispatch = useDispatch()
    console.log(items);

    const [show, setShow] = useState(true)
    const [dataCity, setDataCity] = useState(items)

    function showCitys() {
        dispatch({
            typeof: "STATE"
        })
        setShow(!show)
    }

    // function deleteCity() {
    //     for (let i of citys) {
    //         if (i.id === items.id) {
    //             citys.splice(citys.indexOf(i), 1);
    //         }
    //     }
    //     showCitys();
    // }

    function deleteCity(el) {
        dispatch({
            type: 'DELETE_CITY',
            payload: {
                id: items[0]
            }
        })
    }

    let showCard = <div><CityList /></div>

    if (show) {
        showCard =
            <div className='showcity'>
                <div className='close'><CloseIcon onClick={() => showCitys()} /></div>
                <div className='showcity-name'>
                    < AutorenewIcon sx={{ fontSize: 30 }} onClick={() => console.log('update')} cursor='pointer' />
                    {items[0].city}
                    <DeleteForeverIcon color="warning" sx={{ fontSize: 40 }} onClick={() => deleteCity()} cursor='pointer' />
                </div>
                <div className='showcity-description'>
                    <ul className='ul'>
                        <li> temp - {items[0].temp} </li>
                        <li>tempMax - {items[0].tempMax}</li>
                        <li> tempMin - {items[0].tempMin}</li>
                        <li> description - {items[0].description}</li>
                        <li>windSpeed - {items[0].windSpeed}</li>
                        <li>windGust - {items[0].windGust}</li>
                        <li>humidity - {items[0].humidity}</li>
                        <li> pressure - {items[0].pressure}</li>
                    </ul>
                </div>
            </div>
    }

    return (
        <div >{showCard}</div>
    )
}


export default CityWeather;
