import { citys } from '../mock-items.js/sitys'
import { GET_FIRST_DATA } from './actions';

const initialState = []


export const citysReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_FIRST_DATA: {
            const data = action.payload
            return {
                // data: action.payload.data,
                data: {
                    city: data.main.name,
                    temp: 17 + '& degC',
                    tempMax: 26 + '&degC',
                    tempMin: 12 + '&degC',
                    description: 'description',
                    windSpeed: 127,
                    windGust: 54,
                    humidity: 84,
                    pressure: 12,
                    id: 1,
                    flag: false
                }
            }
        }
        default:
            return state;
    }
}
