import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { citys } from '../mock-items.js/sitys';
import { getFirstData } from '../redux/actions';
import CityWeather from './CityWeather';
import { dataCard } from './CityWeather'
import CloseIcon from '@mui/icons-material/Close';
import AutorenewIcon from '@mui/icons-material/Autorenew';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';





function CityList() {
    let dataCard = {
        city: 'Kiyv',
        temp: 17 + '& degC',
        tempMax: 26 + '&degC',
        tempMin: 12 + '&degC',
        description: 'description',
        windSpeed: 127,
        windGust: 54,
        humidity: 84,
        pressure: 12,
        id: 1,
        flag: false
    };

    const [weather, setWeather] = useState(true)

    const dispatch = useDispatch()

    let items = useSelector((state) => state.sitys)
    // console.log(items.el.city);
    console.log(items);

    const [dataCity, setDataCity] = useState(items)



    let card = items.map(el =>
        <Card sx={{ maxWidth: 200 }} onClick={() => showWeather(el)}>
            <div className="cardWrapper" >
                <CardMedia
                    component="img"
                    alt="weather"
                    height="140"
                    src="https://t3.ftcdn.net/jpg/02/11/52/42/360_F_211524227_Ett8aboQvVnROAFtqu3S1pW99Y3Th9vm.jpg"
                />
                <CardContent>
                    <Typography gutterBottom variant="h3" component="div">
                        {el.city}

                    </Typography>
                    <Typography gutterBottom variant="h6" component="div">
                        {el.temp}
                        <br />
                        {el.description}
                    </Typography>
                </CardContent>
            </div>
        </Card >)

    function showWeather(el) {
        // dispatch({
        //     type: 'ONE_CITY',
        //     payload: {
        //         id: el.id
        //     }
        // })
        // console.log(el.id);
        // setWeather(!weather)
        dataCard = el;
        console.log(el);

        // weatherCityId = el.id;
        setWeather(!weather)
    }

    function deleteCity() {
        dispatch({
            type: 'DELETE_CITY',
            payload: {
                id: items[0].id
            }
        })
        console.log(items[0].id);
        // setShow(!show)
    }

    function deleteCity() {
        console.log('delete');
    }
    function showCitys() {
        console.log('show');
    }


    let content =
        <div>
            <div className='showcity'>
                <div className='close'><CloseIcon onClick={() => showCitys()} cursor='pointer' /></div>
                <div className='showcity-name'>
                    < AutorenewIcon sx={{ fontSize: 30 }} onClick={() => console.log('update')} cursor='pointer' />
                    {dataCard.city}
                    <DeleteForeverIcon color="warning" sx={{ fontSize: 40 }} onClick={() => deleteCity()} cursor='pointer' />
                </div>
                <div className='showcity-description'>
                    <ul className='ul'>
                        <li> temp - {dataCard.temp} </li>
                        <li>tempMax - {dataCard.tempMax}</li>
                        <li> tempMin - {dataCard.tempMin}</li>
                        <li> description - {dataCard.description}</li>
                        <li>windSpeed - {dataCard.windSpeed}</li>
                        <li>windGust - {dataCard.windGust}</li>
                        <li>humidity - {dataCard.humidity}</li>
                        <li> pressure - {dataCard.pressure}</li>
                    </ul>
                </div>
            </div>
        </div>



    if (weather) {
        content = < div className='citys' > {card} </div >
    }



    return (
        <div >{content}</div>
    )

}



export default CityList;
