import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { getFirstData } from '../redux/actions';
import CloseIcon from '@mui/icons-material/Close';
import AutorenewIcon from '@mui/icons-material/Autorenew';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';





function CityList() {

    let items = useSelector((state) => state.citys)
    console.log(items);

    let weatherId
    let content
    const [weather, setWeather] = useState(true) // what we rendering
    const [dataCity, setDataCity] = useState([]) // what city open to show full weather
    // const [data, setData] = useState(items)
    // setData(data) 

    const dispatch = useDispatch()

    useEffect(() => {
        getData()
    }, [])



    let card = items.map(el =>
        <Card sx={{ maxWidth: 200 }} onClick={() => showWeather(el)}>
            <div className="cardWrapper" >
                <CardMedia
                    component="img"
                    alt="weather"
                    height="140"
                    src="https://t3.ftcdn.net/jpg/02/11/52/42/360_F_211524227_Ett8aboQvVnROAFtqu3S1pW99Y3Th9vm.jpg"
                />
                <CardContent>
                    <Typography gutterBottom variant="h3" component="div">
                        {el.data.name}

                    </Typography>
                    <Typography gutterBottom variant="h6" component="div">
                        {eldata.main.temp}
                        <br />
                        {el.description}
                    </Typography>
                </CardContent>
            </div>
        </Card >)

    function deleteCity() {
        dispatch({
            type: 'DELETE_CITY',
            payload: dataCity.id
        })
        console.log(dataCity.id);
        showCitys()
    }
    function showCitys() {
        console.log('show');
        setWeather(!weather)
    }

    function showWeather(el) {
        setDataCity(el)
        setWeather(!weather)
    }

    function getData() {
        fetch('https://api.openweathermap.org/data/2.5/weather?q=Kharkiv&units=metric&APPID=bcf45506de72468c2a0cb7f9a26542bc')
            .then(response => response.json())
            .then(data => dispatch({
                type: 'ADD_CITY',
                payload: { data }
            }))
    }


    weather ?
        content = < div className='citys' > {card} </div > :
        content = < div >
            <div className='showcity'>
                <div className='close'><CloseIcon onClick={() => showCitys()} cursor='pointer' /></div>
                <div className='showcity-name'>
                    < AutorenewIcon sx={{ fontSize: 30 }} onClick={() => console.log('update')} cursor='pointer' />
                    {dataCity.name}
                    <DeleteForeverIcon color="warning" sx={{ fontSize: 40 }} onClick={() => deleteCity()} cursor='pointer' />
                </div>
                <div className='showcity-description'>
                    <ul className='ul'>
                        <li> temp - {dataCity.temp} </li>
                        <li>tempMax - {dataCity.tempMax}</li>
                        <li> tempMin - {dataCity.tempMin}</li>
                        <li> description - {dataCity.description}</li>
                        <li>windSpeed - {dataCity.windSpeed}</li>
                        <li>windGust - {dataCity.windGust}</li>
                        <li>humidity - {dataCity.humidity}</li>
                        <li> pressure - {dataCity.pressure}</li>
                    </ul>
                </div>
            </div>
        </div >


    return (
        <div >{content}</div>
    )

}



export default CityList;
