import { useState } from "react";
import { useSelector, useDispatch } from 'react-redux';


const Reset = () => {

    const [data, setData] = useState(dataBegin);

    const dataBegin = useSelector(state => state.table.data);

    const dataReset = () => {
        setData(useSelector(state => state.table.data))
    };

    return (
        <button onClick={dataReset}>Reset</button>
    )
};

export default Reset;
