import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { citys } from '../mock-items.js/sitys';
import { getFirstData } from '../redux/actions';
import CityWeather from './CityWeather';




function CityList() {

    const [weather, setWeather] = useState(true)

    const dispatch = useDispatch()

    let items = useSelector((state) => state.sitys)
    console.log(items.city);

    const [dataCity, setDataCity] = useState(items)


    // useEffect(() => {
    //     dispatch(getFirstData())
    // }, [])

    // useEffect(() => {
    //     dispatch(getFirstData())
    //     setDataCity(dataCity);
    // }, [])


    let card = items.map(el =>
        <Card sx={{ maxWidth: 200 }} onClick={() => showWeather(el)}>
            <div className="cardWrapper" >
                <CardMedia
                    component="img"
                    alt="weather"
                    height="140"
                    src="https://t3.ftcdn.net/jpg/02/11/52/42/360_F_211524227_Ett8aboQvVnROAFtqu3S1pW99Y3Th9vm.jpg"
                />
                <CardContent>
                    <Typography gutterBottom variant="h3" component="div">
                        {el.city}

                    </Typography>
                    <Typography gutterBottom variant="h6" component="div">
                        {el.temp}
                        <br />
                        {el.description}
                    </Typography>
                </CardContent>
            </div>
        </Card >)

    function showWeather(el) {
        dispatch({
            type: 'ONE_CITY',
            payload: {
                id: el.id
            }
        })
        setWeather(!weather)
    }




    let content = <div><CityWeather /></div>
    if (weather) {
        content = < div className='citys' > {card} </div >
    }



    return (
        <div >{content}</div>
    )

}



export default CityList;
