export const getData = () => dispatch => {
    fetch('https://itrex-react-lab-files.s3.eu-central-1.amazonaws.com/react-test-api.json')
        .then(response => response.json())
        .then(data => {
            const filter = data.map(item => item.adress.state);
            dispatch({ type: 'GET_DATA_SUCCESSFULL', payload: { data, filter } })
        }
        );
}

export const setFilter = (item) => dispatch => {
    data =
        dispatch({ type: 'GET_DATA_FILTER', payload: data })
};
