import { useEffect } from "react";
import { useSelector, useDispatch } from 'react-redux';
import { getData, setFilter } from "../redux/actions";


const FilterTable = () => {
    const dispatch = useDispatch();
    const filter = useSelector(state => state.table.filter);

    useEffect(() => {
        dispatch(getData())
    }, [dispatch])

    function uniqueItem(arr) {
        let result = [];
        for (let key in arr) {
            if (!result.includes(key)) {
                result.push(arr);
            }
        }
        return result;
    }

    return (
        <form type="checkbox" id="myCheck" >
            {/* onclick={() => dispatch(setFilter(item))}
             onMouse={(e) => console.log(e.target.innerText)}> */}
            <select id="select_" onChange={(e) => console.log(e.target.innerText)}>
                <option value="css" >select state</option>

                {uniqueItem(filter).map(item => (
                    <option value="css" >{item}</option>
                ))}
            </select>
        </form >
    );
}

export default FilterTable;
