import { citys } from '../mock-items.js/sitys'
import { GET_FIRST_DATA, ADD_CITY, DELETE_CITY } from './actions';

const initialState = []


export const citysReducer = (state = initialState, action) => {
    switch (action.type) {
        // case GET_FIRST_DATA: {
        //     return {
        //         data: action.payload.data,
        //     }
        // }
        case ADD_CITY: {
            const item = action.payload
            return [...state, item]
        }
        case DELETE_CITY: {
            const { id } = action.payload
            return [...state.filter(el => el.id !== id)]
        }

        default:
            return state;
    }
}
