import { useEffect } from "react";
import { useSelector, useDispatch } from 'react-redux';
import { getData, setFilter } from "../redux/actions";


const FilterTable = () => {
    const dispatch = useDispatch();
    const filter = useSelector(state => state.table.filter);

    useEffect(() => {
        dispatch(getData())
    }, [dispatch])

    function uniqueItem(arr) {
        let result = [];
        for (let i = 0; i < arr.length; i++) {
            if (!result.includes(arr[i])) {
                result.push(arr[i]);
            }
        }
        return result;
    }
    function myFunction() {
        let x = document.getElementById("fname");
        x.value = x.value.toUpperCase();
    }

    return (
        <form type="checkbox" id="myCheck" onChange={myFunction}>
            <select id="select_" >
                <option value="css" >select state</option>
                {uniqueItem(filter).map(item => (
                    <option value="css" >{item}</option>
                ))}
            </select>
        </form >
    );
}

export default FilterTable;
