import React, { useState } from "react";
import CityList from './CitysList'
import { citys } from '../mock-items.js/sitys'
import AutorenewIcon from '@mui/icons-material/Autorenew';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import CloseIcon from '@mui/icons-material/Close';
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";


function CityWeather() {

    let items = useSelector((state) => state.sitys)
    const dispatch = useDispatch()

    const [show, setShow] = useState(true)

    const showCity = items

    function showCitys() {
        showCity[0].flag = !showCity[0].flag;
        setShow(!show)
    }

    // function deleteCity() {
    //     for (let i of citys) {
    //         if (i.id === showCity[0].id) {
    //             citys.splice(citys.indexOf(i), 1);
    //         }
    //     }
    //     showCitys();
    // }

    function deleteCity() {
        dispatch({
            type: 'DELETE_CITY',
            payload: {
                id: showCity[0].id,
            }
        })
    }

    let showCard = <div><CityList /></div>

    if (show) {
        showCard =
            <div className='showcity'>
                <div className='close'><CloseIcon onClick={() => showCitys()} /></div>
                <div className='showcity-name'>
                    < AutorenewIcon sx={{ fontSize: 30 }} onClick={() => console.log('update')} cursor='pointer' />
                    {showCity.city}
                    <DeleteForeverIcon color="warning" sx={{ fontSize: 40 }} onClick={() => deleteCity()} cursor='pointer' />
                </div>
                <div className='showcity-description'>
                    {/* <ul className='ul'>
                        <li> temp - {showCity[0].temp} </li>
                        <li>tempMax - {showCity[0].tempMax}</li>
                        <li> tempMin - {showCity[0].tempMin}</li>
                        <li> description - {showCity[0].description}</li>
                        <li>windSpeed - {showCity[0].windSpeed}</li>
                        <li>windGust - {showCity[0].windGust}</li>
                        <li>humidity - {showCity[0].humidity}</li>
                        <li> pressure - {showCity[0].pressure}</li>
                    </ul> */}
                </div>
            </div>
    }

    return (
        <div >{showCard}</div>
    )
}


export default CityWeather;
